begin
wwv_flow_imp_shared.create_flow_process(
 p_id=>wwv_flow_imp.id(103945495828191427007)
,p_process_sequence=>1
,p_process_point=>'AFTER_LOGIN'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'ID and ROLE fetch process'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'   v_user_role VARCHAR2(255);',
'   v_ID VARCHAR2(255);',
'begin',
'    BEGIN',
'        SELECT TEACHER_ID,''TEACHER'' ',
'        INTO v_ID,v_user_role',
'        FROM TBL_TEACHER',
'         WHERE UPPER(EMAIL) = UPPER(:APP_USER);',
'    EXCEPTION WHEN NO_DATA_FOUND THEN ',
'            BEGIN',
'                SELECT STUDENT_ID,''STUDENT'' ',
'                INTO v_ID,v_user_role',
'                FROM TBL_STUDENT',
'                WHERE UPPER(EMAIL) = UPPER(:APP_USER);',
'            EXCEPTION WHEN NO_DATA_FOUND THEN',
'                v_ID:= NULL;',
'               v_user_role:=''ADMIN'';',
'            END;',
'    END;',
'    :APP_USER_ID:=v_ID;',
'    :APP_USER_ROLE :=v_user_role;',
'END;',
'',
''))
,p_process_clob_language=>'PLSQL'
,p_version_scn=>15665940020671
);
end;
