' FUNCTION my_authentication (',
'    p_username IN VARCHAR2,',
'    p_password IN VARCHAR2',
') RETURN BOOLEAN',
'IS',
'    l_salt        VARCHAR2(4000);',
'    l_hash        VARCHAR2(4000);',
'    l_test VARCHAR2(4000);',
'begin',
'    begin',
'        SELECT PWD_SALT, PWD_HASH',
'        into   l_salt, l_hash',
'        FROM TBL_TEACHER',
'        WHERE UPPER(EMAIL) = UPPER(p_username);',
'',
'        l_test := APEX_UTIL.GET_HASH(',
'        p_values => APEX_T_VARCHAR2(p_password, l_salt),',
'        p_salted => FALSE',
'        );',
'',
'        if l_test=l_hash then ',
'            return true;',
'        else',
'            return false;',
'        end if;',
'',
'    exception when no_data_found then',
'        BEGIN',
'            SELECT PWD_SALT, PWD_HASH',
'            into   l_salt, l_hash',
'            FROM TBL_STUDENT',
'            WHERE UPPER(EMAIL) = UPPER(p_username);',
'            l_test := APEX_UTIL.GET_HASH(',
'            p_values => APEX_T_VARCHAR2(p_password, l_salt),',
'            p_salted => FALSE',
'            );',
'',
'            if l_test=l_hash then ',
'                return true;',
'            else',
'                return false;',
'            end if;',
'        EXCEPTION WHEN NO_DATA_FOUND THEN',
'                l_salt := DBMS_RANDOM.STRING (''A'', 16);',
'',
'                l_hash := APEX_UTIL.GET_HASH(',
'                p_values => APEX_T_VARCHAR2(''ADMIN'', l_salt),',
'                p_salted => FALSE',
'            );',
'            l_test := APEX_UTIL.GET_HASH(',
'            p_values => APEX_T_VARCHAR2(p_password, l_salt),',
'            p_salted => FALSE',
'            );',
'',
'            if l_test=l_hash AND p_username=''ADMIN'' then ',
'                return true;',
'            else',
'                return false;',
'            end if;',
'        END;',
'    END;',
'',
