,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT',
'    d.DNAME AS DEPARTMENT_NAME,',
'    t.TEACHER_ID,',
'    t.FIRST_NAME || '' '' || t.LAST_NAME AS TEACHER_NAME,',
'    t.EMAIL,',
'    TO_CHAR(t.HIRE_DATE, ''DD-MON-YYYY'') AS HIRE_DATE,',
'    t.SUBJECT,',
'    t.GENDER,',
'    t.MOBILE,',
'    t.STATUS,',
'    MAX(sys.dbms_lob.getlength(t."PHOTO")) AS PHOTO_SIZE,',
'    t.PHOTO_FILENAME,',
'    t.PHOTO_MIMETYPE,',
'    NVL(COUNT(DISTINCT s.STUDENT_ID), 0) AS STUDENT_COUNT,',
'    NVL(COUNT(DISTINCT p.PROJECT_ID), 0) AS PROJECT_COUNT,',
'    CASE ',
'        WHEN t.STATUS = ''ACTIVE'' THEN ''Currently Teaching''',
'        WHEN t.STATUS = ''INACTIVE'' THEN ''Not Teaching''',
'        ELSE ''Unknown''',
'    END AS TEACHER_STATUS_DESC,',
'    CASE ',
'        WHEN t.STATUS = ''ACTIVE'' THEN ''currently-teaching''',
'        WHEN t.STATUS = ''INACTIVE'' THEN ''not-teaching''',
'        ELSE ''unknown''',
'    END AS TEACHER_STATUS_DESC_CSS',
'FROM ',
'    TBL_DEPARTMENT d',
'JOIN ',
'    TBL_TEACHER t ON d.DID = t.DID',
'LEFT JOIN ',
'    TBL_STUDENT s ON t.TEACHER_ID = s.TEACHER_ID',
'LEFT JOIN ',
'    TBL_PROJECTS p ON p.CREATED_BY = t.TEACHER_ID',
'                  AND p.DEPARTMENT_ID = d.DID',
'WHERE ',
'    d.DID = :P2_DEPT_ID',
'GROUP BY ',
'    d.DNAME,',
'    t.TEACHER_ID,',
'    t.FIRST_NAME,',
'    t.LAST_NAME,',
'    t.EMAIL,',
'    t.HIRE_DATE,',
'    t.SUBJECT,',
'    t.GENDER,',
'    t.MOBILE,',
'    t.STATUS,',
'    t.PHOTO_FILENAME,',
'    t.PHOTO_MIMETYPE',
