,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT ',
'    P.PROJECT_ID, ',
'    P.DEPARTMENT_ID, ',
'    P.PROJECT_NAME, ',
'    P.DESCRIPTION,',
'    ''from '' || TO_CHAR(P.CREATED_DATE, ''DD-MON-YYYY'') || ',
'    '' to '' || TO_CHAR(P.SUBMISSION_DATE, ''DD-MON-YYYY'') AS DATE_RANGE, ',
'    P.STATUS,',
'    CASE ',
'        WHEN ',
'            P.STATUS != ''INACTIVE'' ',
'            AND ',
'                NOT EXISTS ( ',
'                    SELECT ',
'                        1 ',
'                    FROM ',
'                        TBL_PROJECT_SUBMISSION S ',
'                    WHERE ',
'                        S.PROJECT_ID = P.PROJECT_ID ',
'                    AND ',
'                        S.STUDENT_ID = :P6_STUDENT_ID ',
'                    AND ',
'                        S.STATUS = ''SUBMITTED'' ',
'                ) ',
'            THEN ''Y'' -- Show "Submit" button ',
'        ELSE ',
'            ''N'' -- Hide button ',
'    END AS SHOW_BUTTON',
'FROM ',
'    TBL_PROJECTS P',
'JOIN ',
'    TBL_DEPARTMENT D ',
'    ON D.DID = P.DEPARTMENT_ID',
'WHERE',
'    D.DNAME = :P6_DEPARTMENT_NAME',
'    AND NOT EXISTS (',
'        SELECT 1',
'        FROM TBL_PROJECT_SUBMISSION S',
'        WHERE S.PROJECT_ID = P.PROJECT_ID',
'        AND S.STUDENT_ID = :P6_STUDENT_ID',
'        AND S.STATUS = ''SUBMITTED''',
'    )',
'ORDER BY ',
'    P.CREATED_DATE DESC',
';',
''))
