,p_plug_template=>2072724515482255512
,p_plug_display_sequence=>10
,p_plug_display_point=>'SUB_REGIONS'
,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'-- Overall Attendance',
'SELECT ''Overall Attendance'' AS kpi_title,',
'       ROUND(COUNT(CASE WHEN AD.STATUS = ''PRESENT'' THEN 1 END) ',
'             / NULLIF(COUNT(*),0) * 100, 2) || ''%'' AS kpi_value,',
'       NULL AS kpi_body,',
'       CASE ',
'         WHEN ROUND(COUNT(CASE WHEN AD.STATUS = ''PRESENT'' THEN 1 END) ',
'                    / NULLIF(COUNT(*),0) * 100, 2) >= 85 THEN ''good''',
'         WHEN ROUND(COUNT(CASE WHEN AD.STATUS = ''PRESENT'' THEN 1 END) ',
'                    / NULLIF(COUNT(*),0) * 100, 2) >= 75 THEN ''warning''',
'         ELSE ''bad''',
'       END AS css_class',
'FROM   TBL_ATTENDANCE_DETAIL AD',
'       JOIN TBL_ATTENDANCE A ON A.ATTENDANCE_ID = AD.ATTENDANCE_ID',
'WHERE  AD.STUDENT_ID = :P6_STUDENT_ID',
'',
'UNION ALL',
'-- Best Month',
'SELECT ''Best Month'' AS kpi_title,',
'       percent_attend || ''%'' AS kpi_value,',
'       month_name AS kpi_body,',
'       CASE ',
'         WHEN percent_attend >= 85 THEN ''good''',
'         WHEN percent_attend >= 75 THEN ''warning''',
'         ELSE ''bad''',
'       END AS css_class',
'FROM (',
'    SELECT TO_CHAR(A.ATTEND_DATE, ''FMMonth YYYY'') AS month_name,',
'           ROUND(COUNT(CASE WHEN AD.STATUS = ''PRESENT'' THEN 1 END) ',
'                 / NULLIF(COUNT(*),0) * 100, 2) AS percent_attend,',
'           RANK() OVER (ORDER BY ROUND(COUNT(CASE WHEN AD.STATUS = ''PRESENT'' THEN 1 END) ',
'                 / NULLIF(COUNT(*),0) * 100, 2) DESC) AS rnk',
'    FROM   TBL_ATTENDANCE_DETAIL AD',
'           JOIN TBL_ATTENDANCE A ON A.ATTENDANCE_ID = AD.ATTENDANCE_ID',
'    WHERE  AD.STUDENT_ID = :P6_STUDENT_ID',
'    GROUP BY TO_CHAR(A.ATTEND_DATE, ''FMMonth YYYY'')',
')',
'WHERE rnk = 1 AND :P6_STATUSFLAG != ''INACTIVE''',
'',
'UNION ALL',
'-- Lowest Month',
'SELECT ''Lowest Month'' AS kpi_title,',
'       percent_attend || ''%'' AS kpi_value,',
'       month_name AS kpi_body,',
'       CASE ',
'         WHEN percent_attend >= 85 THEN ''good''',
'         WHEN percent_attend >= 75 THEN ''warning''',
'         ELSE ''bad''',
'       END AS css_class',
'FROM (',
'    SELECT TO_CHAR(A.ATTEND_DATE, ''FMMonth YYYY'') AS month_name,',
'           ROUND(COUNT(CASE WHEN AD.STATUS = ''PRESENT'' THEN 1 END) ',
'                 / NULLIF(COUNT(*),0) * 100, 2) AS percent_attend,',
'           RANK() OVER (ORDER BY ROUND(COUNT(CASE WHEN AD.STATUS = ''PRESENT'' THEN 1 END) ',
'                 / NULLIF(COUNT(*),0) * 100, 2) ASC) AS rnk',
'    FROM   TBL_ATTENDANCE_DETAIL AD',
'           JOIN TBL_ATTENDANCE A ON A.ATTENDANCE_ID = AD.ATTENDANCE_ID',
'    WHERE  AD.STUDENT_ID = :P6_STUDENT_ID',
'    GROUP BY TO_CHAR(A.ATTEND_DATE, ''FMMonth YYYY'')',
')',
'WHERE rnk = 1 AND :P6_STATUSFLAG != ''INACTIVE'';',
''))
,p_lazy_loading=>false
