'    INTO',
'        :P6_FULL_NAME,',
'        :P6_EMAIL,',
'        :P6_DOB,',
'        :P6_GENDER,',
'        :P6_TEACHER_NAME,',
'        :P6_ENROLL_DATE,',
'        :P6_STATUS,',
'        :P6_DEPARTMENT_NAME,',
'        :P6_AGE,',
'        :P6_PARENT_NAME,',
'        :P6_RELATIONSHIP,',
'        :P6_EMERGENCY_CONTACT,',
'        :P6_ADDRESS',
'    FROM ',
'        TBL_STUDENT S',
'    JOIN',
'        TBL_TEACHER T',
'    ON ',
'        S.TEACHER_ID = T.TEACHER_ID',
'    JOIN',
'        TBL_DEPARTMENT D',
'    ON',
'        S.DID = D.DID',
'    WHERE ',
