,p_process_sequence=>30
,p_process_point=>'BEFORE_HEADER'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'SCORE_DETAILS'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'BEGIN',
'    SELECT ',
'        sem.SEMESTER_NO,',
'        ',
'        -- Attendance mark (out of 100)',
'        ROUND(',
'            (SUM(CASE WHEN ad.STATUS = ''PRESENT'' THEN 1 ELSE 0 END) / COUNT(ad.DETAIL_ID)) * 100,',
'            2',
'        ) AS ATTENDANCE_MARK,',
'        ',
'        -- Average project marks',
'        ROUND(AVG(pm.GIVEN_MARKS), 2) AS PROJECT_MARK',
'    INTO',
'        :P6_SEMESTER_NO,',
'        :P6_ATTENDANCE_MARK,',
'        :P6_PROJECT_MARK',
'    FROM ',
'        TBL_STUDENT s',
'        JOIN TBL_ATTENDANCE_DETAIL ad ON ad.STUDENT_ID = s.STUDENT_ID',
'        JOIN TBL_ATTENDANCE a ON a.ATTENDANCE_ID = ad.ATTENDANCE_ID',
'        JOIN TBL_SEMESTER sem ON a.SEMESTER_ID = sem.SEMESTER_ID',
'        LEFT JOIN TBL_PROJECT_SUBMISSION ps ON ps.STUDENT_ID = s.STUDENT_ID',
'        LEFT JOIN TBL_PROJECT_MARKS pm ON pm.SUBMISSION_ID = ps.SUBMISSION_ID',
'',
'    WHERE ',
unistr('        TRUNC(SYSDATE) BETWEEN sem.START_DATE AND sem.END_DATE   -- \2705 Only current semester'),
'        AND s.STUDENT_ID = :P6_STUDENT_ID',
'',
'    GROUP BY ',
'        s.STUDENT_ID, sem.SEMESTER_NO;',
'',
unistr('    -- \2705 Calculate Total Marks'),
'    :P6_TOTAL_MARK := NVL(:P6_ATTENDANCE_MARK, 0) + NVL(:P6_PROJECT_MARK, 0);',
'            ',
'EXCEPTION WHEN NO_DATA_FOUND THEN ',
'    NULL;',
'END;',
''))
,p_process_clob_language=>'PLSQL'
,p_internal_uid=>102481180192615937210
);
