'SELECT ',
'    P.PROJECT_ID,',
'    P.DEPARTMENT_ID,',
'    P.PROJECT_NAME,',
'    P.DESCRIPTION,',
'    P.CREATED_BY,',
'    P.CREATED_DATE,',
'    P.STATUS,',
'    P.SUBMISSION_DATE,',
'    S.DESCRIPTION AS SEMESTER,',
'    TO_DATE(P.SUBMISSION_DATE, ''MM/DD/YYYY'') - TO_DATE(P.CREATED_DATE, ''MM/DD/YYYY'') || '' DAYS'' AS ALLOWED_PERIOD,',
'',
'    -- Total Students in department',
'    (SELECT COUNT(*)',
'     FROM TBL_STUDENT ST',
'     WHERE ST.DID = P.DEPARTMENT_ID) AS TOTAL_STUDENTS,',
'',
'    -- Students who submitted this project',
'    (SELECT COUNT(DISTINCT PS.STUDENT_ID)',
'     FROM TBL_PROJECT_SUBMISSION PS',
'     JOIN TBL_STUDENT STU ON STU.STUDENT_ID = PS.STUDENT_ID',
'     WHERE PS.PROJECT_ID = P.PROJECT_ID',
'       AND STU.DID = P.DEPARTMENT_ID) AS SUBMITTED_STUDENTS,',
'',
'    -- Non submitted = total - submitted',
'    (SELECT COUNT(*)',
'     FROM TBL_STUDENT ST',
'     WHERE ST.DID = P.DEPARTMENT_ID)',
'    -',
'    (SELECT COUNT(DISTINCT PS.STUDENT_ID)',
'     FROM TBL_PROJECT_SUBMISSION PS',
'     JOIN TBL_STUDENT STU ON STU.STUDENT_ID = PS.STUDENT_ID',
'     WHERE PS.PROJECT_ID = P.PROJECT_ID',
'       AND STU.DID = P.DEPARTMENT_ID) AS NON_SUBMITTED_STUDENTS',
