'JOIN',
'    TBL_TEACHER T ON P.CREATED_BY = T.TEACHER_ID',
'JOIN',
'    TBL_DEPT_SEMESTER DS ON DS.DEPT_ID = P.DEPARTMENT_ID',
'JOIN',
'    TBL_SEMESTER S ON S.SEMESTER_ID = DS.SEMESTER_ID         ',
'WHERE',
'    P.DEPARTMENT_ID = :P7_DID ',
'AND ',
'    P.CREATED_BY = :P7_TEACHER_ID',
