,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT ',
'    lt.leave_type_name AS title,',
'    ''Allowed: '' || lt.max_days_allowed || ',
'    '' | Taken: '' || ',
'    NVL((',
'        SELECT SUM(TO_DATE(lr.to_date) - TO_DATE(lr.from_date) + 1)',
'        FROM tbl_leave_request lr',
'        WHERE lr.leave_type_code = lt.leave_type_code',
'          AND lr.teacher_id = :P7_TEACHER_ID',
'          AND EXTRACT(YEAR FROM lr.from_date) = EXTRACT(YEAR FROM SYSDATE)',
'          AND lr.status = ''APPROVED''',
'    ), 0) || ',
'    '' | Remaining: '' || ',
'    (lt.max_days_allowed - NVL((',
'        SELECT SUM(TO_DATE(lr.to_date) - TO_DATE(lr.from_date) + 1)',
'        FROM tbl_leave_request lr',
'        WHERE lr.leave_type_code = lt.leave_type_code',
'          AND lr.teacher_id = :P7_TEACHER_ID',
'          AND EXTRACT(YEAR FROM lr.from_date) = EXTRACT(YEAR FROM SYSDATE)',
'          AND lr.status = ''APPROVED''',
'    ), 0)) AS description,',
'    ',
'    CASE lt.leave_type_code',
'        WHEN ''SICK'' THEN ''fa-briefcase-medical''',
'        WHEN ''EMERGENCY'' THEN ''fa-bolt''',
'        WHEN ''COMPENSATORY OFF'' THEN ''fa-clock''',
'        WHEN ''CASUAL'' THEN ''fa-user-clock''',
'        WHEN ''VACATION'' THEN ''fa-umbrella-beach''',
'        ELSE ''fa-calendar''',
'    END AS icon_class,',
'    ',
'    NULL AS icon_source,',
'    NULL AS link',
'',
'FROM tbl_leave_type lt;',
''))
