,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT ',
'    st.STUDENT_ID,',
'    st.FIRST_NAME || '' '' || st.LAST_NAME AS STUDENT_NAME,',
'    sem.SEMESTER_NO,',
'    COUNT(ad.DETAIL_ID) AS TOTAL_CLASSES,',
'    SUM(CASE WHEN ad.STATUS = ''PRESENT'' THEN 1 ELSE 0 END) AS PRESENT_DAYS,',
'    ROUND(',
'        (SUM(CASE WHEN ad.STATUS = ''PRESENT'' THEN 1 ELSE 0 END) / ',
'         COUNT(ad.DETAIL_ID)) * 100, 2',
'    ) AS ATTENDANCE_MARK',
'FROM',
'    TBL_ATTENDANCE_DETAIL ad',
'JOIN',
'    TBL_ATTENDANCE a ',
'        ON a.ATTENDANCE_ID = ad.ATTENDANCE_ID',
'JOIN',
'    TBL_STUDENT st ',
'        ON st.STUDENT_ID = ad.STUDENT_ID',
'JOIN',
'    TBL_SEMESTER sem ',
'        ON sem.SEMESTER_ID = a.SEMESTER_ID',
'WHERE',
'    TRUNC(a.ATTEND_DATE) BETWEEN sem.START_DATE AND LEAST(TRUNC(SYSDATE), sem.END_DATE)',
'AND',
'    a.TEACHER_ID = :P7_TEACHER_ID',
'GROUP BY',
'    st.STUDENT_ID, st.FIRST_NAME, st.LAST_NAME, sem.SEMESTER_NO',
'ORDER BY',
