,p_source_type=>'NATIVE_SQL_REPORT'
,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT ',
'    st.STUDENT_ID,',
'    st.FIRST_NAME || '' '' || st.LAST_NAME AS STUDENT_NAME,',
'    COUNT(ps.SUBMISSION_ID) AS TOTAL_SUBMISSIONS,',
'    SUM(',
'        CASE ',
'            WHEN pm.MARK_ID IS NOT NULL THEN 1 ',
'            ELSE 0 ',
'        END',
'    ) AS MARKED_COUNT,',
'    ROUND(',
'        NVL(AVG(pm.GIVEN_MARKS), 0), 2',
'    ) AS PROJECT_MARKS,        -- Average mark (out of 100)',
'    MAX(pm.MARKED_ON) AS LAST_MARKED_ON',
'FROM',
'    TBL_PROJECT_SUBMISSION ps',
'JOIN',
'    TBL_STUDENT st ',
'        ON st.STUDENT_ID = ps.STUDENT_ID',
'LEFT JOIN',
'    TBL_PROJECT_MARKS pm ',
'        ON pm.SUBMISSION_ID = ps.SUBMISSION_ID',
'WHERE',
'    ps.SUBMITTED_ON BETWEEN ',
'        (SELECT START_DATE FROM TBL_SEMESTER WHERE TRUNC(SYSDATE) BETWEEN START_DATE AND END_DATE)',
'        AND ',
'        (SELECT END_DATE FROM TBL_SEMESTER WHERE TRUNC(SYSDATE) BETWEEN START_DATE AND END_DATE)',
'AND',
'    pm.TEACHER_ID = :P7_TEACHER_ID        ',
'GROUP BY',
'    st.STUDENT_ID, st.FIRST_NAME, st.LAST_NAME',
'ORDER BY',
'    PROJECT_MARKS DESC',
