'      :P22_STUDENT_ID,',
'      :P22_LEAVE_TYPE,',
'      :P22_FROM_DATE,',
'      :P22_TO_DATE,',
'      :P22_REASON,',
'      ''PENDING'',',
'      SYSDATE,',
'      l_file.blob_content,',
'      l_file.mime_type,',
'      l_file.filename,',
'      SYSDATE',
