'            THEN (TRUNC(SYSDATE) - DUE_DATE) * 50',
'        ELSE 0',
'    END AS FINE,',
'',
'    -- Total payable = Amount + Fine',
'    AMOUNT + CASE',
'                   WHEN DUE_DATE < TRUNC(SYSDATE)',
'                       THEN (TRUNC(SYSDATE) - DUE_DATE) * 50',
'                   ELSE 0',
'               END AS TOTAL_PAYABLE ',
'INTO ',
'    :P24_FEE_ID, ',
'    :P24_SEMESTER_NO_1, ',
'    :P24_AMOUNT, ',
'    :P24_DUE_DATE, ',
'    :P24_DESCRIPTION_1,',
'    :P24_FINE,',
'    :P24_TOTAL_PAYABLE ',
'FROM ',
'    TBL_FEES_MASTER ',
'WHERE ',
'    SEMESTER_NO = :P24_SEMESTER_NO ',
