,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'New'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT ',
'    s.FIRST_NAME || '' '' || s.LAST_NAME AS STUDENT_NAME,',
'    d.DNAME,',
'    f.AMOUNT,',
'    NVL(r.FEES_STATUS,''UNPAID'') AS FEES_STATUS,',
'    r.PAYMENT_DATE',
'INTO',
'    :P25_STUDENT_NAME,',
'    :P25_DNAME,',
'    :P25_AMOUNT,',
'    :P25_FEE_STATUS,',
'    :P25_PAYMENT_DATE',
'',
'FROM ',
'    TBL_STUDENT s',
'JOIN ',
'    TBL_DEPARTMENT d ON d.DID = s.DID',
'JOIN ',
'    TBL_DEPT_SEMESTER ds ON ds.DEPT_ID = s.DID',
'JOIN ',
'    TBL_SEMESTER sem ON sem.SEMESTER_ID = ds.SEMESTER_ID',
'JOIN ',
'    TBL_FEES_MASTER f ON f.SEMESTER_NO = sem.SEMESTER_NO',
'LEFT JOIN ',
'    STUDENT_NEXT_FEES_DESC r ON r.STUDENT_ID = s.STUDENT_ID AND r.SEMESTER_NO = f.SEMESTER_NO',
'WHERE',
'    s.STUDENT_ID = :P25_STUDENT_ID',
